mapper.select:
select * from distributingcases di,register re,chit ch,chitdetails cd,tb_company tb,client cl,clientdetails ct where di.registerid=re.registerid and di.chitid=ch.chitid and ch.chitid=cd.chitid and tb.chitid=ch.chitid and cl.tcid=tb.tcid and cl.clientid=ct.clientid

    and re.batch=#{pch}


    and cl.clientname=#{cxrxm}


    and cl.clientsfz=#{cxrzj}


    and di.distributingcasesstatic=#{zt}
